        var listenerCb    = listener ? listener : this.errorListener;
        httpChain.add(httpChain.getWrapped(listenerCb));
        var afterFilters  = this.getFilters(url.pathname, 'after');
        httpChain.addAll(afterFilters);
        httpChain.next(req, res);
    };
    if(method == 'post') {
        var body;
